import React, { Component } from 'react';
import '../style.css';
import StickyFooter from 'react-sticky-footer';
import play from '../icons/play.png';
import pause from '../icons/pause.png';
import loop from '../icons/loop.png';
import prev from '../icons/prev.png';
import volume from '../icons/volume.png';
import mute from '../icons/mute.png';
import next from '../icons/next.png';
import stop from '../icons/stop.png';
import noBodyCanSaveMeNow from "../LP/1.mp3";
import goodBye from "../LP/2.mp3";
import ttm from "../LP/3.mp3";
import bs from "../LP/4.mp3";
import invisible from "../LP/5.mp3";

class Bottomplayer extends Component{

  state = {
    selectedTrack: null,
    player: "stopped",
    currentTime: null,
    duration: null
  };

  componentDidMount() {
    this.player.addEventListener("timeupdate", e => {
      this.setState({
        currentTime: e.target.currentTime,
        duration: e.target.duration
      });
    });
  }

  componentWillUnmount() {
    this.player.removeEventListener("timeupdate", () => {});
  }

  componentDidUpdate(prevProps, prevState) {
    if (this.state.selectedTrack !== prevState.selectedTrack) {
      let track;
      switch (this.state.selectedTrack) {
        case "Nobody can save me now":
          track = noBodyCanSaveMeNow;
          break;
        case "Good Goodbye":
          track = goodBye;
          break;
        case "Talking To Myself":
          track = ttm;
          break;
        case "Battle Symphony":
          track = bs;
          break;
        case "Invisible":
          track = invisible;
          break;
        default:
          break;
      }
      if (track) {
        this.player.src = track;
        console.log(this.player.src)
        this.player.play();
        this.setState({ player: "playing", duration: this.player.duration });
      }
    }
    if (this.state.player !== prevState.player) {
      if (this.state.player === "paused") {
        this.player.pause();
      } else if (this.state.player === "stopped") {
        this.player.pause();
        this.player.currentTime = 0;
        this.setState({ selectedTrack: null });
      } else if (
        this.state.player === "playing" &&
        prevState.player === "paused"
      ) {
        this.player.play();
      }
    }
  }

    render(){
        const list = [
            { id: 1, title: "Nobody can save me now" },
            { id: 2, title: "Good Goodbye" },
            { id: 3, title: "Talking To Myself" },
            { id: 4, title: "Battle Symphony" },
            { id: 5, title: "Invisible" }
          ].map(item => {
            return (
              <li
                key={item.id}
                onClick={() => this.setState({ selectedTrack: item.title })}
              >
                {item.title}
              </li>
            );
        });
        
        return (
          <StickyFooter
          bottomThreshold={50}
          normalStyles={{
            height: "80px",
          backgroundColor: "#999999",
          padding: "2rem"
          }}
          stickyStyles={{
          backgroundColor: "rgba(255,255,255,.8)",
          height: "75px",
          padding: "2rem"
          }}
          >
           <img src={prev} />
           <img src={play} onClick={() => this.setState({ player: "playing" })} />
           <img src={next} />
           <img src={stop} onClick={() => this.setState({ player: "stopped" })} />
           <img className="leftimg" src={volume} />
           <img src={loop} />
           </StickyFooter>
        )
    }
}

export default Bottomplayer;
