import React, { Component } from 'react';
import AudioPlayer from "react-modular-audio-player";
import ReactDOM from "react-dom";

// Data
var data = {
    title: 'How To Be A Human Being',
    artist: 'Glass Animals',
    artwork: 'https://dhgkpqsiufwl2.cloudfront.net/media/ArticleSharedImage/imageFull/.fDcKYrUV/ArticleSharedImage-63604.jpg',
    tracks: [
        {
            id: 1,
            title: 'Life Itself',
            duration: '4:41'
        },
        {
            id: 2,
            title: 'Youth',
            duration: '3:51'
        },
        {
            id: 3,
            title: 'Season 2 Episode 3',
            duration: '4:04'
        },
        {
            id: 4,
            title: 'Pork Soda',
            duration: '4:14'
        },
        {
            id: 5,
            title: 'Mama\'s Gun',
            duration: '4:27'
        },
        {
            id: 6,
            title: 'Cane Shuga',
            duration: '3:17'
        },
        {
            id: 7,
            title: '[Premade Sandwiches]',
            duration: '0:36'
        },
        {
            id: 8,
            title: 'The Other Side of Paradise',
            duration: '5:21'
        },
        {
            id: 9,
            title: 'Take A Slice',
            duration: '3:50'
        },
        {
            id: 10,
            title: 'Poplar St',
            duration: '4:23'
        },
        {
            id: 11,
            title: 'Agnes',
            duration: '4:32'
        }
    ]
};

// App
var Musicplayer = React.createClass({
    getDefaultProps: function() {
        return (data);
    },
    getInitialState: function() {
        return {
            playing: false,
            currentTrack: [
                {
                    id: 0,
                    title: '',
                    duration: '0:00'
                }
            ]
        };
    },
    handleClick: function(e) {
        console.log(e.target.id);
        if(e.target.id === 'play') {
            this.setState({ playing: true });
        } else {
            this.setState({ playing: false });
        }
        this.forceUpdate();
    },
    render: function() {
        return (
            <div className="App">
                <Artwork onClick={this.handleClick} playing={this.state.playing} artwork={this.props.artwork} />
                <TrackList tracks={this.props.tracks} />
            </div>
        );
    }
});

// Artwork
var Artwork = React.createClass({
    render: function() {
        return (
            <div className="Artwork" style={{ backgroundImage: 'url(' + this.props.artwork + ')' }}>
            <Controls onClick={this.props.onClick} playing={this.props.playing} />
            </div>
        );
    }
});

// Controls
var Controls = React.createClass({
    render: function() {

        if(this.props.playing === false) {
            var playpause = <i id="play" onClick={this.props.onClick} className="fa fa-fw fa-play"></i>;
        } else {
            var playpause = <i id="pause" onClick={this.props.onClick} className="fa fa-fw fa-pause"></i>;
        }

        return (
            <div className="Controls">
                <i className="fa fa-fw fa-fast-backward"></i>
                {playpause}
                <i className="fa fa-fw fa-fast-forward"></i>
            </div>
        );
    }
});

// TrackList
var TrackList = React.createClass({
    render: function() {

        var tracks = this.props.tracks.map(function(track, i) {
            return (
                <li>
                    <div className="number">{track.id}</div>
                    <div className="title">{track.title}</div>
                    <div className="duration">{track.duration}</div>
                </li>
            );
        });
        return (
            <ul className="TrackList">
                {tracks}
            </ul>
        );
    }
});

// Render
ReactDOM.render(<Musicplayer />, document.getElementById('music'));
